import React from 'react';
import { Navbar, Nav, Col, Row } from 'react-bootstrap';
import { LinkContainer } from 'react-router-bootstrap'
import { SHOP_NAME } from '../constant';
import logo from '../asset/image/logo.png';
import './navigation.css';

export default class Navigation extends React.Component {
    navbar = { backgroundColor: '#F16E10 !important' };

    constructor(props) {
        super(props);
        this.state = {
           // current position
           pos: window.pageYOffset,
           visible: true
        };
     }
     componentDidMount() {
        window.addEventListener("scroll", this.handleScroll);
     }
     componentWillUnmount() {
        window.removeEventListener("scroll", this.handleScroll);
     }
     // Hide or show the menu.
     handleScroll = () => {
        const { pos } = this.state;
        // updated position
        const temp = window.pageYOffset;
        const visible = pos > temp;
        this.setState({
           pos: temp,
           visible
        })
    }


    render() {
        console.log("asdacd")
        return <Navbar className={"color-nav " + (!this.state.visible ? "navbarHidden" : " ")} expand="md" sticky="top" collapseOnSelect="true">
            <Navbar.Brand href={process.env.PUBLIC_URL}>
                <img
                    src={logo}
                    width="240"
                    height="40"
                    className="d-inline-block align-top"
                    alt={SHOP_NAME}
                />
            </Navbar.Brand>
            <Navbar.Toggle />
            <Navbar.Collapse id="basic-navbar-nav" >
                <Nav style={{width:"100%"}}>
                    <Col md="12">
                    <Row>
                        <Col md={{offset:1, span:3}}>
                            <LinkContainer to="/">
                                <Nav.Link>Main</Nav.Link>
                            </LinkContainer>
                        </Col>
                        <Col md={{span:3}}>
                            <LinkContainer to="/second">
                                <Nav.Link>SECOND</Nav.Link>
                            </LinkContainer></Col>

                    </Row>
                    </Col>
                </Nav>
            </Navbar.Collapse>
        </Navbar>
    }

}
